text:
&.ng-enter {
    @include transition-e(.5s);
    transform: translate(150px, -50%);
    opacity: 0;
  }
  &.ng-enter-active {
    transform: translate(0px, -50%);
    opacity: 1;
  }
  &.ng-leave {
    opacity: 1;
    transform: translate(0px, -50%);
    @include transition-e(.3s);
  }
  &.ng-leave-active {
    transform: translate(150px, -50%);
    opacity: 0;
  }
